import View from "../core/view";
import { NewsFeedApi } from "../core/api";
import { NewsStore } from "../types";
import { NEWS_URL } from "../config";

const template: string = `
    <div class="bg-gray-600 min-h-screen">
    <div class="bg-white text-xl">
      <div class="mx-auto px-4">
        <div class="flex justify-between items-center py-6">
          <div class="flex justify-start">
            <h1 class="font-extrabold">Hacker News</h1>
          </div>
          <div class="items-center justify-end">
            <a href="#/page/{{__prev_page__}}" class="text-gray-500">
              Previous
            </a>
            <a href="#/page/{{__next_page__}}" class="text-gray-500 ml-4">
              Next
            </a>
          </div>
        </div> 
      </div>
    </div>
    <div class="p-4 text-2xl text-gray-700">
      {{__news_feed__}}        
    </div>
  </div>
      `;

export default class NewsFeedView extends View{ // 클래스를 만든다는 것은 인스턴스를 만들어서 인스턴스에 필요한 정보들을 저장해 뒀다가 필요한 경우에 계속 재활용해서 쓸수 있다는 장점
    private api: NewsFeedApi;
    private store: NewsStore;
    
    constructor(containerId: string, store: NewsStore) {
  
      super(containerId, template);
  
      this.store = store;
      this.api = new NewsFeedApi(NEWS_URL); // 클래스 인스턴스
  
      if (!this.store.hasFeeds) {
        this.store.setFeeds(this.api.getData());
      }
    }
  
    render = (page: string = '1'):void => {
      this.store.currentPage = Number(page);
  
      for (let i = (this.store.currentPage - 1) * 10; i < this.store.currentPage * 10; i++) {
        const {id, title, comments_count, user, points, time_ago, read} = this.store.getFeed(i);
        this.addHtml(`
        <div class="p-6 ${
          read ? "bg-red-500" : "bg-white"
        } mt-6 rounded-lg shadow-md transition-colors duration-500 hover:bg-green-100">
        <div class="flex">
          <div class="flex-auto">
            <a href="#/show/${id}">${title}</a>  
          </div>
          <div class="text-center text-sm">
            <div class="w-10 text-white bg-green-300 rounded-lg px-0 py-2">${
              comments_count
            }</div>
          </div>
        </div>
        <div class="flex mt-3">
          <div class="grid grid-cols-3 text-sm text-gray-500">
            <div><i class="fas fa-user mr-1"></i>${user}</div>
            <div><i class="fas fa-heart mr-1"></i>${points}</div>
            <div><i class="far fa-clock mr-1"></i>${time_ago}</div>
          </div>  
        </div>
      </div>    
        `);
      }
    
      this.setTemplateDate("news_feed", this.getHtml());
      this.setTemplateDate("prev_page", String(this.store.prevPage));
      this.setTemplateDate("next_page", String(this.store.nextPage));
    
      this.updateView();
      }
  }
